import React from 'react';
import PropTypes from 'prop-types';

import './Loading.css';

class Loading extends React.Component {
    render() {
        if (this.props.type === 'line-scale') {
            return (
                <div style={this.props.style}>
                    <div className="la-line-scale la-dark la-sm">
                        <div></div>
                        <div></div>
                        <div></div>
                        <div></div>
                        <div></div>
                    </div>
                </div>
            );
        } else {
            return (
                <div>
                    ...
                </div>
            );
        }
    }
}

Loading.propTypes = {
    type: PropTypes.string,
    style: PropTypes.object
};

export { Loading };
